    @Test
    fun testJwtVcPresentationParsing() = runTest {
        init()
        val jwtVp1 =
            "eyJraWQiOiJkaWQ6a2V5OnpEbmFlWWI3RGFrUVdtWWtyTGttc1ZFUkFhekY1WWExRzVueGJTblFjTEpaOENyMTcjekRuYWVZYjdEYWtRV21Za3JMa21zVkVSQWF6RjVZYTFHNW54YlNuUWNMSlo4Q3IxNyIsInR5cCI6IkpXVCIsImFsZyI6IkVTMjU2In0..Od6DX18rTycJQxsdgozwgN3NVXUkti-e_OpJig75Kn18VOKaFl98y5KnPE3KIa5rFfmU_fyzWKzkPtvP9oQqBw"
        val presentation = JwtVcJsonPresentation.parse(jwtVp1)
            .getOrThrow()
        println("W3C Presentation: $presentation")
        presentation.presentationVerification(
            expectedAudience = "verifier2-example",
            expectedNonce = "cfd1cabf-081e-4e7b-9751-6fbd5722e88e"
        )
    }

