    @Test
    fun testDcql() = runTest {
        customCredentialTest(
            policies = flowOf(
                HolderPolicy(
                    priority = 1,
                    description = "Block if sharing address",
                    check = DcqlHolderPolicyCheck(
                        dcqlQuery = Json.decodeFromString<DcqlQuery>(
                            //language=JSON
                            """
                    {
                      "credentials": [
                        {
                          "id": "pid",
                          "format": "dc+sd-jwt",
                          "meta": {
                            "vct_values": ["https://credentials.example.com/identity_credential"]
                          },
                          "claims": [
                            {"path": ["given_name"]},
                            {"path": ["family_name"]},
                            {"path": ["address", "street_address"]}
                          ]
                        },
                        {
                          "id": "mdl",
                          "format": "mso_mdoc",
                          "meta": {
                            "doctype_value": "org.iso.7367.1.mVRC"
                          },
                          "claims": [
                            {"path": ["org.iso.7367.1", "vehicle_holder"]},
                            {"path": ["org.iso.18013.5.1", "first_name"]}
                          ]
                        }
                    ]
                    }
                """.trimIndent()
                        )
                    ),
                    action = HolderPolicyAction.BLOCK
                ),
                HolderPolicy(
                    priority = 5,
                    description = "Allow if sharing birthdate",
                    check = DcqlHolderPolicyCheck(
                        dcqlQuery = Json.decodeFromString<DcqlQuery>(
                            //language=JSON
                            """
                    {
                      "credentials": [
                        {
                          "id": "pid",
                          "format": "dc+sd-jwt",
                          "meta": {
                            "vct_values": ["https://credentials.example.com/identity_credential"]
                          },
                          "claims": [
                            {"path": ["given_name"]},
                            {"path": ["family_name"]},
                            {"path": ["birthdate"]}
                          ]
                        },
                        {
                          "id": "mdl",
                          "format": "mso_mdoc",
                          "meta": {
                            "doctype_value": "org.iso.7367.1.mVRC"
                          },
                          "claims": [
                            {"path": ["org.iso.7367.1", "vehicle_holder"]},
                            {"path": ["org.iso.18013.5.1", "first_name"]}
                          ]
                        }
                    ]
                    }
                """.trimIndent()
                        )
                    ),
                    action = HolderPolicyAction.ALLOW
                )
            ),
            credentials = flowOf(sdJwtVcSignedExample2),
            expected = HolderPolicyAction.ALLOW
        )
    }

