package id.walt.mdoc

import id.walt.crypto.keys.KeySerialization
import id.walt.mdoc.parser.MdocParser

suspend fun main() {
    val doc = MdocParser.parseToDocument(
        ""
    )
    val p = doc.issuerSigned.getParsedIssuerAuth()
    val x5 = p.x5c.joinToString { "X5CCertificateString(\"$it\")" }
    println("x5cList = X5CList(listOf($x5)),")
    val serK = KeySerialization.serializeKey(p.signerKey)
    println("signerKey = runBlocking { KeyManager.resolveSerializedKey(\"\"\"$serK\"\"\") }")
}
