package id.walt.policies

import id.walt.credentials.utils.VCFormat
import id.walt.did.dids.DidService
import id.walt.did.dids.resolver.LocalResolver
import id.walt.policies.models.PolicyRequest.Companion.parsePolicyRequests
import kotlinx.coroutines.test.runTest
import kotlinx.serialization.encodeToString
import kotlinx.serialization.json.Json
import kotlinx.serialization.json.JsonObject
import kotlinx.serialization.json.jsonArray
import kotlinx.serialization.json.jsonObject
import kotlin.test.Test

class PresentationVerificationTest {
    @Test
    fun testPresentationVerification()= runTest {
        DidService.apply {
            registerResolver(LocalResolver())
            updateResolversForMethods()
        }

        val vpToken =
            "eyJhbGciOiJFZERTQSIsInR5cCI6IkpXVCIsImtpZCI6ImRpZDprZXk6ejZNa3BkQ3FUNWZIWlZBYmRRdjJTS1dGTWdyTHN2UmFZdVRmSnpDdkJ4TG4xWHBzI3o2TWtwZENxVDVmSFpWQWJkUXYyU0tXRk1nckxzdlJhWXVUZkp6Q3ZCeExuMVhwcyJ9..-29z2twNHmK3tIwS59R-WiHuOhBNJbUS5YXKPCbCaKhPa8QyD1Z8hZ-G6ECFY8K4ZSnoB5b7OCyvvIclYj8gAA"


        //language=json
        val vpPolicies = Json.parseToJsonElement(
            """
        [
          "signature",
          "expired",
          "not-before"
        ]
    """
        ).jsonArray.parsePolicyRequests()

        //language=json
        val vcPolicies = Json.parseToJsonElement(
            """
       [
          "signature",
          "expired",
          "not-before"
        ] 
    """
        ).jsonArray.parsePolicyRequests()

        //language=json
        val specificPolicies = Json.parseToJsonElement(
            """
       {
          "OpenBadgeCredential": [
              {
                "policy": "schema",
                "args": {
                    "type": "object",
                    "required": ["issuer"],
                    "properties": {
                        "issuer": {
                            "type": "object"
                        }
                    }
                }
            }
          ]
       } 
    """
        ).jsonObject.mapValues { it.value.jsonArray.parsePolicyRequests() }

        println("VP Policies: $vpPolicies")
        println("VC Policies: $vcPolicies")
        println("SP Policies: $specificPolicies")

        val r = Verifier.verifyPresentation(
          VCFormat.jwt_vp_json,
            vpToken = vpToken, vpPolicies = vpPolicies, globalVcPolicies = vcPolicies, specificCredentialPolicies = specificPolicies, mapOf(
                "presentationSubmission" to JsonObject(emptyMap()), "challenge" to "abc"
            )
        )

        println(
            Json { prettyPrint = true }.encodeToString(
                r
            )
        )

        val x = r.results.flatMap { it.policyResults }
        println("Results: " + x.size)
        println("OK: ${x.count { it.isSuccess() }}")
    }
}
