    @Test
    fun testDeviceRequestDeserialization() {
        // try deserializing example from ISO/IEC FDIS 18013-5: D.4.1.1 mdoc request
        val exampleRequest =
            ""
        val devRequest = DeviceRequest.fromCBORHex(exampleRequest)
        assertEquals(expected = "1.0", actual = devRequest.version.value)
        assertEquals(expected = 1, actual = devRequest.docRequests.size)
        assertEquals(expected = "org.iso.18013.5.1.mDL", actual = devRequest.docRequests[0].docType)
        assertContains(iterable = devRequest.docRequests[0].nameSpaces, element = "org.iso.18013.5.1")
        assertEquals(
            expected = mapOf(
                "family_name" to true,
                "document_number" to true,
                "driving_privileges" to true,
                "issue_date" to true,
                "expiry_date" to true,
                "portrait" to false
            ),
            actual = devRequest.docRequests[0].getRequestedItemsFor("org.iso.18013.5.1")
        )
    }

    @Test
    fun testDeviceRequestBuilderAndSerialization() {
        val docReq = MDocRequestBuilder("org.iso.18013.5.1.mDL")
            .addDataElementRequest("org.iso.18013.5.1", "family_name", true)
            .addDataElementRequest("org.iso.18013.5.1", "birth_date", false)
            .build()

        val deviceRequest = DeviceRequest(listOf(docReq))
        val devReqCbor = deviceRequest.toCBORHex()
        println("DEVICE REQUEST: $devReqCbor")

        val parsedDevReq = DeviceRequest.fromCBORHex(devReqCbor)
        assertEquals(expected = deviceRequest.version, actual = parsedDevReq.version)
        assertEquals(expected = 1, actual = parsedDevReq.docRequests.size)
        val parsedDocReq = parsedDevReq.docRequests[0]
        assertEquals(expected = docReq.docType, actual = parsedDocReq.docType)
        assertEquals(expected = true, actual = parsedDocReq.nameSpaces.containsAll(docReq.nameSpaces))
        assertEquals(expected = docReq.itemsRequest, actual = parsedDocReq.itemsRequest)
        assertEquals(expected = docReq.decodedItemsRequest.docType, actual = parsedDocReq.decodedItemsRequest.docType)
        assertEquals(
            expected = docReq.decodedItemsRequest.nameSpaces,
            actual = parsedDocReq.decodedItemsRequest.nameSpaces
        )
    }

    @Test
    fun testVerifyDeviceMac() {
        // try deserializing example from ISO/IEC FDIS 18013-5: D.4.1.2 mdoc response
        val serializedDoc =
            ""
        val mdocRespParsed = DeviceResponse.fromCBORHex(serializedDoc)
        val deviceMacParsed = mdocRespParsed.documents[0].deviceSigned?.deviceAuth?.deviceMac
        assertNotEquals(illegal = null, actual = deviceMacParsed)
        assertEquals(expected = HMAC256, actual = deviceMacParsed!!.algorithm)
        assertEquals(expected = null, actual = deviceMacParsed.payload)
        assertContentEquals(
            expected = Hex.decode("E99521A85AD7891B806A07F8B5388A332D92C189A7BF293EE1F543405AE6824D"),
            actual = deviceMacParsed.signatureOrTag
        )

        // using key and device authentication from ISO/IEC FDIS 18013-5: D.5.3 mdoc authentication
        val deviceAuthenticationBytes = Hex.decode(
            ""
        )
        val deviceAuthentication =
            DataElement.fromCBOR<EncodedCBORElement>(deviceAuthenticationBytes).decode<DeviceAuthentication>()
        assertEquals(expected = "DeviceAuthentication", actual = deviceAuthentication.data[0].value.toString())

        val ephemeralMacKey = Hex.decode("dc2b9566fdaaae3c06baa40993cd0451aeba15e7677ef5305f6531f3533c35dd")

        // test verify by attaching payload and verifying mac
        assertEquals(
            expected = true,
            actual = deviceMacParsed.attachPayload(deviceAuthenticationBytes).verify(ephemeralMacKey)
        )

        // test presenting mdoc with same key and device authentication, to check if result is the same
        val deviceSignedByUs = mdocRespParsed.documents[0].let {
            it.presentWithDeviceMAC(MDocRequestBuilder(it.docType.value).build(), deviceAuthentication, ephemeralMacKey)
        }.deviceSigned

        assertNotEquals(illegal = null, actual = deviceSignedByUs)
        assertNotEquals(illegal = null, actual = deviceSignedByUs!!.deviceAuth.deviceMac)
        assertContentEquals(
            expected = deviceMacParsed.signatureOrTag,
            actual = deviceSignedByUs.deviceAuth.deviceMac!!.signatureOrTag
        )
    }
}
